INSERT INTO `post_language` (`post_id`, `language_id`, `name`, `description`, `content`, `meta_title`, `meta_keyword`, `meta_description`, `canonical`, `created_at`, `updated_at`) VALUES
(8, 1, 'CHÍNH SÁCH BẢO HÀNH', NULL, '<p>Để đảm bảo sự h&agrave;i l&ograve;ng tuyệt đối của kh&aacute;ch h&agrave;ng khi mua sắm tại vphome24..com, ch&uacute;ng t&ocirc;i nhận bảo h&agrave;nh sản phẩm trong 3 th&aacute;ng, 6 th&aacute;ng, 12 th&aacute;ng t&ugrave;y từng sản phẩm v&agrave; tất cả c&aacute;c sản phẩm đều được bảo h&agrave;nh lỗi 1 đổi 1 trong v&ograve;ng 7 ng&agrave;y kể từ khi kh&aacute;ch h&agrave;ng nhận sản phẩm.</p>\r\n\r\n<p>Qu&yacute; kh&aacute;ch vui l&ograve;ng kiểm tra thật kỹ h&agrave;ng ho&aacute;, đối chiếu sản phẩm khi được tư vấn, phiếu bảo h&agrave;nh (nếu c&oacute;) trước khi nhận h&agrave;ng nhằm đảm bảo quyền lợi của Qu&yacute; kh&aacute;ch trong việc Đổi/Trả h&agrave;ng v&agrave; Bảo h&agrave;nh sản phẩm, đồng thời li&ecirc;n hệ với bộ phận Bảo h&agrave;nh theo th&ocirc;ng tin để biết chi tiết về t&igrave;nh trạng bảo h&agrave;nh của sản phẩm.</p>\r\n\r\n<p>Để đảm bảo sự h&agrave;i l&ograve;ng tuyệt đối của kh&aacute;ch h&agrave;ng khi mua sắm tại vphome24..com, ch&uacute;ng t&ocirc;i nhận bảo h&agrave;nh sản phẩm trong 3 th&aacute;ng, 6 th&aacute;ng, 12 th&aacute;ng t&ugrave;y từng sản phẩm v&agrave; tất cả c&aacute;c sản phẩm đều được bảo h&agrave;nh lỗi 1 đổi 1 trong v&ograve;ng 7 ng&agrave;y kể từ khi kh&aacute;ch h&agrave;ng nhận sản phẩm.</p>\r\n\r\n<p><strong>Trường hợp được bảo h&agrave;nh</strong></p>\r\n\r\n<p>● Sản phẩm bị trục trặc, lỗi do nh&agrave; sản xuất.</p>\r\n\r\n<p>● Sản phẩm c&ograve;n nguy&ecirc;n vẹn, kh&ocirc;ng bị nứt vỡ, kh&ocirc;ng bị biến dạng do t&aacute;c động của ngoại lực.</p>\r\n\r\n<p>● Sản phẩm kh&ocirc;ng c&oacute; dấu hiệu bị ẩm, ngập&nbsp;nước dẫn đến chập&nbsp;chạm mạch.</p>\r\n\r\n<p>● Sản phẩm phải c&ograve;n nguy&ecirc;n vẹn th&ocirc;ng tin S/N v&agrave; tem của nh&agrave; ph&acirc;n phối c&ograve;n nguy&ecirc;n vẹn với đầy đủ th&ocirc;ng tin thời gian bảo h&agrave;nh.</p>\r\n\r\n<p>● Sản phẩm phải c&ograve;n trong thời gian bảo h&agrave;nh.</p>\r\n\r\n<p>● Phải c&oacute; h&oacute;a đơn mua h&agrave;ng ki&ecirc;m phiếu bảo h&agrave;nh ghi r&otilde; ng&agrave;y th&aacute;ng mua v&agrave; k&iacute;ch hoạt sản phẩm.</p>\r\n\r\n<p>● Sản phẩm phải x&oacute;a hết th&ocirc;ng tin, ngắt kết nối khỏi t&agrave;i khoản c&aacute; nh&acirc;n.</p>\r\n\r\n<p>● Thiết bị kh&ocirc;ng thuộc c&aacute;c điều kiện trong mục từ chối bảo h&agrave;nh.</p>\r\n\r\n<p><strong>Trường hợp từ chối bảo h&agrave;nh</strong></p>\r\n\r\n<p>Sản phẩm c&oacute; dấu hiệu tự &yacute; th&aacute;o rời, c&oacute; tem bảo h&agrave;nh nhưng bị bong tr&oacute;c, bị r&aacute;ch, tem bảo h&agrave;nh bị d&aacute;n đ&egrave;, tem bảo h&agrave;nh bị sửa đổi, mờ, kh&ocirc;ng ch&iacute;nh x&aacute;c&hellip;Kh&aacute;ch h&agrave;ng kh&ocirc;ng cung cấp được Phiếu bảo h&agrave;nh hoặc cung cấp sai số điện thoại mua h&agrave;ng so với số điện thoại tr&ecirc;n hệ thống bảo h&agrave;nh điện tử của vphome24.</p>\r\n\r\n<p>● Tự &yacute; sửa chữa sản phẩm bởi c&aacute;c c&aacute; nh&acirc;n hay tổ chức b&ecirc;n ngo&agrave;i trung t&acirc;m bảo h&agrave;nh của vphome24.</p>\r\n\r\n<p>● Hỏng do thi&ecirc;n tai, hoả hoạn, nguồn điện kh&ocirc;ng b&igrave;nh thường, bị ch&aacute;y nổ&hellip;</p>\r\n\r\n<p>● Sản phẩm c&oacute; dấu hiệu đ&atilde; bị c&ocirc;n tr&ugrave;ng x&acirc;m nhập, điều kiện bảo quản k&eacute;m.</p>\r\n\r\n<p>● Bị hư hỏng do sơ suất c&aacute; nh&acirc;n như để sản phẩm nhiễm nước, hoặc tiếp x&uacute;c với c&aacute;c h&oacute;a chất g&acirc;y ăn m&ograve;n g&acirc;y hư hỏng, lắp đặt sai quy c&aacute;ch, kh&ocirc;ng đ&uacute;ng hướng dẫn, sử dụng linh kiện kh&ocirc;ng ph&ugrave; hợp để sạc pin, sản phẩm bị lỗi do qu&yacute; kh&aacute;ch h&agrave;ng vận chuyển.</p>\r\n\r\n<p>● Sản phẩm bị rơi, bị va đập, m&oacute;p, nứt thủng, biến dạng dẫn đến lỗi.</p>\r\n\r\n<p>● Sản phẩm kh&ocirc;ng được mua từ hệ thống ph&acirc;n phối ch&iacute;nh thức của vphome24.</p>\r\n\r\n<p>● Sản phẩm hết thời gian bảo h&agrave;nh.</p>\r\n\r\n<p>● Sản phẩm bị đứt ngầm, g&atilde;y do t&aacute;c động từ b&ecirc;n ngo&agrave;i.</p>\r\n\r\n<p>● Một số sản phẩm sẽ c&oacute; t&iacute;nh năng, dung lượng bị hao hụt vật l&yacute; theo thời gian sử dụng (pin, pin dự ph&ograve;ng&hellip;) cũng sẽ&nbsp;kh&ocirc;ng được bảo h&agrave;nh những lỗi li&ecirc;n quan đến những hao hụt đ&oacute;.</p>\r\n\r\n<p>● Kh&ocirc;ng bảo h&agrave;nh những lỗi do cảm nhận c&aacute; nh&acirc;n.</p>\r\n\r\n<p>● Những lỗi thuộc về nguy&ecirc;n l&yacute; chung, đặc điểm c&ocirc;ng nghệ, thuật to&aacute;n của nh&agrave; sản xuất&hellip;. cũng sẽ kh&ocirc;ng được bảo h&agrave;nh.</p>\r\n\r\n<p>V&iacute; dụ như: pin sử dụng theo thời gian sẽ c&oacute; sự hao hụt về dung lượng theo thời gian; đồng hồ định vị sẽ c&oacute; sai số trong ph&eacute;p đo định vị t&ugrave;y v&agrave;o từng c&ocirc;ng nghệ định vị v&agrave; vị tr&iacute; địa l&yacute; tại thời điểm đ&oacute; cũng như sử dụng Sim của nh&agrave; mạng n&agrave;o. Th&ocirc;ng thường c&ocirc;ng nghệ định vị GPS sai số từ 5 đến 30m; WIFI sai số từ 50m đến 200m; LBS sai số từ 100m trở l&ecirc;n.</p>\r\n\r\n<p>Lưu &yacute;: Một số sản phẩm được vphome24 cung cấp c&oacute; trang bị chuẩn kh&aacute;ng nước. Tuy nhi&ecirc;n đ&oacute; chỉ l&agrave; một trang bị để bảo về thiết bị tốt hơn v&agrave; n&oacute; kh&ocirc;ng phải l&agrave; chuẩn chống nước tuyệt đối. T&iacute;nh năng chống nước, chống bụi kh&ocirc;ng phải điều kiện vĩnh viễn. Khả năng chống nước bị hao m&ograve;n l&agrave; chuyện b&igrave;nh thường. Ch&iacute;nh v&igrave; thế ch&uacute;ng t&ocirc;i khuyến c&aacute;o kh&aacute;ch h&agrave;ng kh&ocirc;ng n&ecirc;n mang thiết bị khi đi bơi, tắm hay ng&acirc;m v&agrave;o nước v&agrave; c&aacute;c dung dịch chất lỏng kh&aacute;c. Nếu thiết bị của kh&aacute;ch h&agrave;ng v&agrave;o nước sẽ kh&ocirc;ng nằm trong danh mục lỗi được bảo h&agrave;nh.</p>\r\n\r\n<p><strong>Thời gian bảo h&agrave;nh</strong></p>\r\n\r\n<p>C&aacute;c sản phẩm b&aacute;n ra&nbsp;c&oacute; thời gian bảo h&agrave;nh căn cứ theo th&ocirc;ng b&aacute;o tr&ecirc;n Website: vphome24.com v&agrave; c&oacute; phiếu b&aacute;n h&agrave;ng ki&ecirc;m phiếu bảo h&agrave;nh. Đ&acirc;y l&agrave; thời gian bảo h&agrave;nh cho lỗi của m&aacute;y do nh&agrave; sản xuất g&acirc;y ra hoặc lỗi phần mềm của sản phẩm.</p>\r\n\r\n<p>● Bảo h&agrave;nh si&ecirc;u tốc 1 đổi 1: Qu&yacute; kh&aacute;ch được đổi ngay sản phẩm mới 100% trong thời gian 7 ng&agrave;y kể từ ng&agrave;y mua hoặc nhận được sản phẩm nếu sản phẩm bị lỗi do sản xuất v&agrave; thỏa m&atilde;n điều kiện bảo h&agrave;nh.</p>\r\n\r\n<p>● Sản phẩm mua ngo&agrave;i thời hạn 7 ng&agrave;y sẽ được xử l&yacute; trong thời gian từ 2-15 ng&agrave;y (trừ chủ nhật v&agrave; c&aacute;c ng&agrave;y lễ, Tết). H&agrave;ng h&oacute;a sẽ được sửa chữa hoặc đổi sang sản phẩm tương đương c&ugrave;ng loại. Thời gian bảo h&agrave;nh c&oacute; thể sớm hơn quy định. Tuy nhi&ecirc;n, trong trường hợp đến thời hạn hẹn trả bảo h&agrave;nh m&agrave; ch&uacute;ng t&ocirc;i chưa c&oacute; h&agrave;ng trả, vphome24 sẽ t&iacute;nh th&ecirc;m thời hạn bảo h&agrave;nh cho số ng&agrave;y qu&aacute; hạn.</p>\r\n\r\n<p>● Trường hợp bất khả kh&aacute;ng sản phẩm của Qu&yacute; kh&aacute;ch vẫn nằm trong thời gian bảo h&agrave;nh v&agrave; vẫn đủ điều kiện để bảo h&agrave;nh nhưng vphome24 kh&ocirc;ng thể sửa chữa hoặc kh&ocirc;ng c&oacute; h&agrave;ng tương đương thay thế, hai b&ecirc;n c&oacute; thể thoả thuận để t&igrave;m giải ph&aacute;p tối ưu h&agrave;i h&ograve;a lợi &iacute;ch cho cả người b&aacute;n lẫn người mua.</p>\r\n\r\n<p>● Đổi sản phẩm: Trong thời gian 3 ng&agrave;y kể từ ng&agrave;y mua, nhận được h&agrave;ng, Qu&yacute; kh&aacute;ch c&oacute; nhu cầu đổi sang sản phẩm kh&aacute;c. vphome24 sẽ hỗ trợ đổi cho kh&aacute;ch h&agrave;ng m&agrave; kh&ocirc;ng t&iacute;nh th&ecirc;m ph&iacute; đổi h&agrave;ng. H&agrave;ng được đổi y&ecirc;u cầu phải c&ograve;n nguy&ecirc;n vẹn, kh&ocirc;ng xước x&aacute;t, kh&ocirc;ng biến dạng, đầy đủ hộp v&agrave; phụ kiện đi k&egrave;m v&agrave; chưa qua sử dụng. (Sản phẩm đổi phải c&oacute; gi&aacute; trị tương đương hoặc lớn hơn gi&aacute; trị sản phẩm đ&atilde; mua trước đ&oacute;). Mất hộp hoặc phụ kiện trừ 10% gi&aacute; trị sản phẩm, kh&ocirc;ng nhập lại qu&agrave; tặng đi k&egrave;m (nếu c&oacute;).</p>\r\n\r\n<p>● Trả sản phẩm: Hỗ trợ trả sản phẩm trong thời gian 7 ng&agrave;y, tuy nhi&ecirc;n khi ho&agrave;n trả sản phẩm phải chịu ph&iacute; ho&agrave;n trả như sau:</p>\r\n\r\n<p>+ Chịu ph&iacute; 10% GI&Aacute; B&Aacute;N đối với sản phẩm ho&agrave;n trả c&oacute; gi&aacute; trị lớn hơn 3 triệu</p>\r\n\r\n<p>+ Chịu ph&iacute; 20% GI&Aacute; B&Aacute;N đối với sản phẩm ho&agrave;n trả c&oacute; gi&aacute; trị trong khoảng từ 1 triệu đến 2 triệu</p>\r\n\r\n<p>+ Chịu ph&iacute; 30% GI&Aacute; B&Aacute;N đối với sản phẩm ho&agrave;n trả c&oacute; gi&aacute; trị Nhỏ hơn 1 triệu</p>\r\n\r\n<p>Lưu &yacute;: Ph&iacute; ho&agrave;n trả l&agrave; trừ % GI&Aacute; B&Aacute;N chứ kh&ocirc;ng phải GI&Aacute; KHUYẾN M&Atilde;I</p>\r\n\r\n<p><strong>Thời gian nhận v&agrave; trả bảo h&agrave;nh</strong></p>\r\n\r\n<p>vphome24 nhận v&agrave; trả bảo h&agrave;nh từ 10h00 đến 17h30 tất cả c&aacute;c ng&agrave;y trong tuần (trừ Chủ nhật v&agrave; c&aacute;c ng&agrave;y Lễ, Tết).</p>\r\n\r\n<p>Nếu Qu&yacute;&nbsp;kh&aacute;ch c&oacute; nhu cầu bảo h&agrave;nh ngo&agrave;i giờ tr&ecirc;n vui l&ograve;ng li&ecirc;n hệ trước vớ Ph&ograve;ng bảo h&agrave;nh của vphome24 theo số m&aacute;y</p>\r\n\r\n<p>0816358452&nbsp;(Từ 8h00 đến 19h00)</p>\r\n\r\n<p>Ch&iacute;nh s&aacute;ch đổi trả h&agrave;ng c&oacute; ghi trong h&oacute;a đơn, Qu&yacute; kh&aacute;ch h&agrave;ng vui l&ograve;ng đọc kỹ để tr&aacute;nh những thắc mắc v&agrave; khiếu nại kh&ocirc;ng đ&uacute;ng với ch&iacute;nh s&aacute;ch của c&ocirc;ng ty. Xin cảm ơn!</p>', NULL, NULL, NULL, 'chinh-sach-bao-hanh', '2024-05-03 10:37:06', '2024-05-03 10:37:06'),
(7, 1, 'CHÍNH SÁCH BẢO MẬT', NULL, '<p>Với phương ch&acirc;m t&ocirc;n trọng v&agrave; bảo vệ quyền lợi kh&aacute;ch h&agrave;ng, gi&uacute;p Qu&yacute; kh&aacute;ch c&oacute; được trải nghiệm mua sắm thoải m&aacute;i, an to&agrave;n cũng như g&acirc;y dựng niềm tin của Qu&yacute; kh&aacute;ch đối với vphome24, ch&uacute;ng t&ocirc;i cam sẽ nỗ lực tối đa để bảo vệ th&ocirc;ng tinQu&yacute; kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p>Với phương ch&acirc;m t&ocirc;n trọng v&agrave; bảo vệ quyền lợi kh&aacute;ch h&agrave;ng, gi&uacute;p Qu&yacute; kh&aacute;ch c&oacute; được trải nghiệm mua sắm thoải m&aacute;i, an to&agrave;n cũng như g&acirc;y dựng niềm tin của Qu&yacute; kh&aacute;ch đối với&nbsp;vphome24, ch&uacute;ng t&ocirc;i cam sẽ nỗ lực tối đa để bảo vệ th&ocirc;ng tin Qu&yacute; kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p>Nội dung ch&iacute;nh s&aacute;ch bảo mật th&ocirc;ng tin của ch&uacute;ng t&ocirc;i c&oacute; thể thay đổi. Để đảm bảo quyền lợi của m&igrave;nh, qu&yacute; kh&aacute;ch vui l&ograve;ng thường xuy&ecirc;n truy cập v&agrave;o&nbsp;vphome24.com&nbsp;để kiểm tra ch&iacute;nh s&aacute;ch bảo mật th&ocirc;ng tin của ch&uacute;ng t&ocirc;i.</p>\r\n\r\n<p><strong>1. Mục đ&iacute;ch thu thập th&ocirc;ng tin:</strong></p>\r\n\r\n<p>&ndash; Hỗ trợ kh&aacute;ch h&agrave;ng khi mua sản phẩm</p>\r\n\r\n<p>&ndash; Giải đ&aacute;p thắc mắc kh&aacute;ch h&agrave;ng</p>\r\n\r\n<p>&ndash; Cung cấp cho bạn th&ocirc;ng tin mới nhất tr&ecirc;n Website của ch&uacute;ng t&ocirc;i</p>\r\n\r\n<p>&ndash; Xem x&eacute;t v&agrave; n&acirc;ng cấp nội dung v&agrave; giao diện của Website</p>\r\n\r\n<p>&ndash; Thực hiện c&aacute;c bản khảo s&aacute;t kh&aacute;ch h&agrave;ng</p>\r\n\r\n<p>&ndash; Thực hiện c&aacute;c hoạt động quảng b&aacute; li&ecirc;n quan đến c&aacute;c sản phẩm v&agrave; dịch vụ của&nbsp;vphome24</p>\r\n\r\n<p><strong>2. Phạm vi thu thập th&ocirc;ng tin:</strong></p>\r\n\r\n<p>&ndash; Để truy cập v&agrave; sử dụng một số dịch vụ tại&nbsp;vphome24, qu&yacute;